# Note: This is the base64 of the image on the gui, check here https://codebeautify.org/base64-to-image-converter or on any base64 decoder, its not some obsure obfuscated code
banner_image_base64 = ""

# Create the main window
root = tk.Tk()
root.title("Wallpaper Engine to Lively 1.2")


def pkg_convert(filename, filepath):
    # Extact all asset if the format is .pkg with RePGK
    if not os.path.isfile("RePKG.zip"):
        # Ask the user to download RePKG (Asset Packge Extrator)
        user_reponse = tk.messagebox.askquestion(title="RePKG Download ?", message="Your wallaper is in a propriatery format, (.pkg) do you want to download RePKG to extact all the assets?")

        if user_reponse == "yes":
            urllib.request.urlretrieve("https://github.com/notscuffed/repkg/releases/download/v0.2.2-alpha/RePKG.zip", "RePKG.zip")
            with zipfile.ZipFile("RePKG.zip", 'r') as zip_repkg: # Unzip the file to copy the video
                zip_repkg.extractall()
                # Start the function when RePKG is downloaded and ready!
                pkg_convert(filename, filepath)
        else:
            tk.messagebox.showerror(title="Invalid File", message="Your file is invalid, Make sure its a video background in the .mp4 format")
    
    else:
        os.system(f"rePKG.exe extract {filename}\scene.pkg")
        subprocess.Popen(rf'explorer /select,"{os.getcwd()}\output\materials"')

        

# Convert the file
def convert():
    file = fd.askopenfilename() # Ask to user for the file
    print(file)
    with zipfile.ZipFile(file, 'r') as zip_ref: # Unzip the file to copy the video
        zip_ref.extractall()
    
    file_name, file_extension = os.path.splitext(file) # Get the folder name 
    just_file_name = os.path.basename(file_name)
    if mp4_files := [f for f in os.listdir(just_file_name) if f.endswith('.mp4')]:
        # Convert the file if the format is .mp4
        shutil.copy(just_file_name + "\\" + mp4_files[0], os.curdir) # Copy the video
        shutil.rmtree(just_file_name) # Delete the old folder
        subprocess.Popen(rf'explorer /select,"{os.getcwd()}\{mp4_files[0]}"')
    else:
        pkg_convert(just_file_name, file)

